sql.where:
 
 and e.`EQUIPMENT_NAME` like #{equipmentName}
 
  
 and e.`GROUP_NO` = #{groupNo}
 
  
 and e.`SUB_GROUP_NO` = #{subGroupNo}
 
and e.DELETE_FLAG = 0

mapper.sql:
	
	    
      and t.`REPAIR_LEVEL` = #{repairLevel}
      
      
      and e.`EQUIPMENT_NAME` like #{equipmentName}
      
       
      and e.`GROUP_NO` = #{groupNo}
      
       
      and e.`SUB_GROUP_NO` = #{subGroupNo}
      
	  
	        and e.DELETE_FLAG = 0

mapper.select:
select e.equipment_no, e.equipment_name, e.GROUP_NO, t.repair_level, t.repair_name, t.company_names, t.TIME_LIMIT, t.REMARK, cg.CONSTANT_NAME as GROUP_NAME, e.SUB_GROUP_NO , d.CONSTANT_NAME as SUB_GROUP_NAME from test.equipment e inner join ( select r.equipment_no as equipment_no,r.repair_level as repair_level , con.CONSTANT_NAME as repair_name, group_concat(c.company_name separator ',') as company_names , r.TIME_LIMIT,r.REMARK from test.report r inner join test.company c on r.COMPANY_NO = c.COMPANY_NO and c.DELETE_FLAG = 0 inner join test.constant con on r.REPAIR_LEVEL = con.CONSTANT_NO and con.DELETE_FLAG = 0 where r.DELETE_FLAG = 0 group by r.equipment_no,r.repair_level,con.CONSTANT_NAME,r.TIME_LIMIT,r.REMARK
) t on e.equipment_no = t.equipment_no and e.DELETE_FLAG = 0 inner join test.constant cg on e.GROUP_NO = cg.CONSTANT_NO and cg.DELETE_FLAG = 0 inner join test.constant d on e.SUB_GROUP_NO = d.CONSTANT_NO and d.DELETE_FLAG = 0
LIMIT ${startNo}, ${pageSize}

select
e.equipment_no, e.equipment_name, e.GROUP_NO, t.repair_level, t.repair_name, t.company_names, t.TIME_LIMIT, t.REMARK, cg.CONSTANT_NAME as GROUP_NAME, e.SUB_GROUP_NO , d.CONSTANT_NAME as SUB_GROUP_NAME from test.equipment e inner join ( select r.equipment_no as equipment_no,r.repair_level as repair_level , con.CONSTANT_NAME as repair_name, group_concat(c.company_name separator ',') as company_names , r.TIME_LIMIT,r.REMARK from test.report r inner join test.company c on r.COMPANY_NO = c.COMPANY_NO and c.DELETE_FLAG = 0 inner join test.constant con on r.REPAIR_LEVEL = con.CONSTANT_NO and con.DELETE_FLAG = 0 where r.DELETE_FLAG = 0 group by r.equipment_no,r.repair_level,con.CONSTANT_NAME,r.TIME_LIMIT,r.REMARK
) t on e.equipment_no = t.equipment_no inner join test.constant cg on e.GROUP_NO = cg.CONSTANT_NO and cg.DELETE_FLAG = 0 inner join test.constant d on e.SUB_GROUP_NO = d.CONSTANT_NO and d.DELETE_FLAG = 0 where e.DELETE_FLAG = 0


where  e.delete_flag = '0'
    
    and e.`equipment_NAME` like #{equipmentName}
    
      
    and e.`GROUP_NO` = #{groupNo}
    
    
    and e.`SUB_GROUP_NO` = #{subGroupNo}
   
   LIMIT ${startNo}, ${pageSize}


where  e.delete_flag = '0'
    
    and e.`equipment_NAME` = #{equipmentName}
    
    
    and e.`GROUP_NO` = #{groupNo}
    
    
    and e.`SUB_GROUP_NO` = #{subGroupNo}

mapper.update:
    UPDATE Equipment e
    SET  
          e.LAST_MODIFY_BY = #{lastModifyBy}
        
         ,e.`EQUIPMENT_NAME` = #{equipmentName}
        
        
	    , e.DELETE_FLAG = #{deleteFlag}
      
        
	    , e.GROUP_NO = #{groupNo}
      
        
	    , e.SUB_GROUP_NO = #{subGroupNo}
      
        
	    , e.REMARK = #{remark}
      
        
	    , e.SORT = #{sort}
      
      WHERE EQUIPMENT_NO = #{equipmentNo}

mapper.delete:
delete from Equipment
WHERE 1 =1
    
    and `remark` = #{remark}
    
    
    and `equipment_No` != #{equipmentNo}
